import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

import Table from './components/Table';
import RadarChart from './components/RadarChart';
import ScatterPlot from './components/ScatterPlot';

const App = () => {
  const [data, setData] = useState([]);

  const [chartType, setChartType] = useState('scatter');

  const handleChartTypeChange = (event) => {
    setChartType(event.target.value);
  };

  useEffect(() => {
    fetch('/species.json')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error('Error fetching the data:', error));
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-6 left-block">
          <h1>Antelope Data Dashboard</h1>
          <select className="form-select" value={chartType} onChange={handleChartTypeChange}>
            <option value="scatter">Scatter Plot</option>
            <option value="radar">Radar Chart</option>
          </select>
          <div className="chart-block">
            {chartType === 'scatter' ? <ScatterPlot data={data} /> : <RadarChart antelopeData={data} />}
          </div>
        </div>
        <div className="col-md-6 right-block">
          <div className="table-block">
            <Table antelopesData={data} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
