import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

const prepareDataForChart = (antelopeData) => {
  const countsByCountryAndHornType = {};

  antelopeData.forEach(({ continent, horns }) => {
    const key = `${continent}_${horns}`;
    if (!countsByCountryAndHornType[key]) {
      countsByCountryAndHornType[key] = 0;
    }
    countsByCountryAndHornType[key]++;
  });

  const hornTypes = [...new Set(antelopeData.map(({ horns }) => horns))];
  const countries = [...new Set(antelopeData.map(({ continent }) => continent))];

  const datasets = countries.map((country) => ({
    label: country,
    data: hornTypes.map((hornType) => {
      const key = `${country}_${hornType}`;
      return countsByCountryAndHornType[key] || 0;
    }),
    borderColor: getRandomColor(),
    backgroundColor: 'rgba(255, 99, 132, 0.2)',
  }));

  return {
    labels: hornTypes,
    datasets: datasets,
  };
};

const getRandomColor = () => {
  return `rgba(${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)}, 1)`;
};

const RadarChart = ({ antelopeData }) => {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    if (chartRef && chartRef.current) {
      const ctx = chartRef.current.getContext('2d');
      if (ctx) {
        const config = {
          type: 'radar',
          data: prepareDataForChart(antelopeData),
          options: {
            responsive: true,
            plugins: {
              title: {
                display: true,
                text: 'Radar Chart',
              },
            },
          },
        };

        if (chartInstance.current) {
          chartInstance.current.destroy();
        }

        chartInstance.current = new Chart(ctx, config);
      }
    }

    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, [antelopeData]);

  return <canvas ref={chartRef} />;
};

export default RadarChart;
